import React from "react";

import { Container, Content, Score, Image } from "./MemoryGame.style";

import fries from "../../assets/images/fries.png";
import cheeseburger from "../../assets/images/cheeseburger.png";
import icecream from "../../assets/images/ice-cream.png";
import pizzar from "../../assets/images/pizza.png";

const MemoryGame = () => {
  const cardArray = [
    {
      name: "fries",
      img: fries,
    },
    {
      name: "cheeseburger",
      img: cheeseburger,
    },
    {
      name: "ice-cream",
      img: icecream,
    },
    {
      name: "pizza",
      img: pizzar,
    },
    {
      name: "milkshake",
      img: "../../assets/images/milkshake.png",
    },
    {
      name: "hotdog",
      img: "../../assets/images/hotdog.png",
    },
    {
      name: "fries",
      img: "../../assets/images/fries.png",
    },
    {
      name: "cheeseburger",
      img: "../../assets/images/cheeseburger.png",
    },
    {
      name: "ice-cream",
      img: "../../assets/images/ice-cream.png",
    },
    {
      name: "pizza",
      img: "../../assets/images/pizza.png",
    },
    {
      name: "milkshake",
      img: "../../assets/images/milkshake.png",
    },
    {
      name: "hotdog",
      img: "../../assets/images/hotdog.png",
    },
  ];

  const renderBoard = () => {
    return cardArray.map((data, index) => <Image key={index} src={data.img} />);
  };

  return (
    <Container>
      <Score>
        Score: <span>1</span>
      </Score>
      <Content>{renderBoard()}</Content>
    </Container>
  );
};

export default MemoryGame;
